mapper.select:
SELECT RA.ID AS requestApprovalId, RA.APPROVAL_ORDER AS approvalOrder, RA.IS_CANCELED AS isCanceled, RA.IS_APPROVED AS isApproved, A.ID AS approvalId, A.CONTENT AS content, A.CREATED_AT AS createdAt, T.ID AS typeId, T.TYPE AS type, A.EMPLOYEE_ID AS requesterId, RA.EMPLOYEE_ID AS approverId, COUNT(*) OVER() AS totalCount FROM tbl_request_approval RA JOIN tbl_approval A ON A.ID = RA.APPROVAL_ID JOIN tbl_approval_type T ON A.TYPE_ID = T.ID WHERE A.EMPLOYEE_ID = #{requesterId} AND RA.APPROVAL_ORDER = (SELECT MAX(APPROVAL_ORDER) FROM tbl_request_approval WHERE APPROVAL_ID = RA.APPROVAL_ID)
AND A.TYPE_ID = #{typeId}
AND A.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND A.CONTENT LIKE CONCAT('%', #{keyword}, '%')
AND RA.IS_APPROVED = #{status}
LIMIT #{offset}, #{limit}

SELECT RA.ID AS requestApprovalId, RA.IS_CANCELED AS isCanceled, RA.IS_APPROVED AS isApproved, A.ID AS approvalId, A.CONTENT AS content, A.CREATED_AT AS createdAt, T.ID AS typeId, T.TYPE AS type, A.EMPLOYEE_ID AS requesterId, RA.EMPLOYEE_ID AS approverId, COUNT(*) OVER() AS totalCount FROM tbl_request_approval RA JOIN tbl_approval A ON A.ID = RA.APPROVAL_ID JOIN tbl_approval_type T ON A.TYPE_ID = T.ID WHERE RA.EMPLOYEE_ID = #{approverId}
AND A.TYPE_ID = #{typeId}
AND A.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND A.CONTENT LIKE CONCAT('%', #{keyword}, '%')
AND RA.IS_APPROVED = #{status}
LIMIT #{offset}, #{limit}
