mapper.select:
SELECT A.ID AS POST_ID, A.TITLE, A.CONTENT, A.POST_IMG, A.CREATED_AT, A.AUTHOR_ID, A.ORIGIN_ID, A.RECENT_ID, A.TAB_RELATION_ID, A.CATEGORY_ID FROM tbl_post A WHERE ((A.ORIGIN_ID IS NULL AND A.RECENT_ID IS NULL AND A.DELETED_AT IS NULL) OR A.ID IN ( SELECT RECENT_ID FROM tbl_post WHERE (ORIGIN_ID IS NULL AND RECENT_ID IS NOT NULL AND DELETED_AT IS NULL) ))
AND A.TAB_RELATION_ID = #{request.tabRelationId}
AND A.TAB_RELATION_ID IN
#{tab}
AND A.CATEGORY_ID = #{request.categoryId}
AND A.CATEGORY_ID IS NULL
AND A.TITLE LIKE CONCAT('%', #{request.title}, '%')
AND A.CONTENT LIKE CONCAT('%', #{request.content}, '%')
AND A.ID IN ( SELECT B.POST_ID FROM tbl_post_tag B JOIN tbl_tag C ON B.TAG_ID = C.ID WHERE C.NAME IN
#{tag}
GROUP BY B.POST_ID HAVING COUNT(DISTINCT C.NAME) = ${request.tags.size} )
ORDER BY COALESCE(A.ORIGIN_ID, A.ID) ASC
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

SELECT Count(A.ID) FROM tbl_post A WHERE ((A.ORIGIN_ID IS NULL AND A.RECENT_ID IS NULL AND A.DELETED_AT IS NULL) OR A.ID IN ( SELECT RECENT_ID FROM tbl_post WHERE (ORIGIN_ID IS NULL AND RECENT_ID IS NOT NULL AND DELETED_AT IS NULL) ))
AND A.TAB_RELATION_ID = #{request.tabRelationId}
AND A.TAB_RELATION_ID IN
#{tab}
AND A.CATEGORY_ID = #{request.categoryId}
AND A.CATEGORY_ID IS NULL
AND A.TITLE LIKE CONCAT('%', #{request.title}, '%')
AND A.CONTENT LIKE CONCAT('%', #{request.content}, '%')
AND A.ID IN ( SELECT B.POST_ID FROM tbl_post_tag B JOIN tbl_tag C ON B.TAG_ID = C.ID WHERE C.NAME IN
#{tag}
GROUP BY B.POST_ID HAVING COUNT(DISTINCT C.NAME) = ${request.tags.size} )

SELECT
       COUNT(*)
  FROM tbl_like A
 WHERE A.EMPLOYEE_ID = #{employeeId}
   AND A.POST_ID = #{postId};

SELECT
       COUNT(*)
  FROM tbl_favorites A
 WHERE A.EMPLOYEE_ID = #{employeeId}
   AND A.POST_ID = #{postId};
